void read_ocm::thread_ap_clk_no_reset_() {
    if ( ap_rst_n_inv.read() == ap_const_logic_1) {
        ap_CS_fsm = ap_ST_fsm_state1;
    } else {
        ap_CS_fsm = ap_NS_fsm.read();
    }
    if ( ap_rst_n_inv.read() == ap_const_logic_1) {
        ap_reg_ioackin_SOURCE_BUS_ARREADY = ap_const_logic_0;
    } else {
        if (esl_seteq<1,1,1>(ap_const_lv1_1, ap_CS_fsm_state2.read())) {
            if (!esl_seteq<1,1,1>(ap_const_logic_0, ap_sig_ioackin_SOURCE_BUS_ARREADY.read())) {
                ap_reg_ioackin_SOURCE_BUS_ARREADY = ap_const_logic_0;
            } else if (esl_seteq<1,1,1>(ap_const_logic_1, SOURCE_BUS_ARREADY.read())) {
                ap_reg_ioackin_SOURCE_BUS_ARREADY = ap_const_logic_1;
            }
        }
    }
    if ( ap_rst_n_inv.read() == ap_const_logic_1) {
        ap_reg_ioackin_SOURCE_BUS_AWREADY = ap_const_logic_0;
    } else {
        if (esl_seteq<1,1,1>(ap_const_lv1_1, ap_CS_fsm_state10.read())) {
            if (!esl_seteq<1,1,1>(ap_const_logic_0, ap_sig_ioackin_SOURCE_BUS_AWREADY.read())) {
                ap_reg_ioackin_SOURCE_BUS_AWREADY = ap_const_logic_0;
            } else if (esl_seteq<1,1,1>(ap_const_logic_1, SOURCE_BUS_AWREADY.read())) {
                ap_reg_ioackin_SOURCE_BUS_AWREADY = ap_const_logic_1;
            }
        }
    }
    if ( ap_rst_n_inv.read() == ap_const_logic_1) {
        ap_reg_ioackin_SOURCE_BUS_WREADY = ap_const_logic_0;
    } else {
        if (esl_seteq<1,1,1>(ap_const_lv1_1, ap_CS_fsm_state11.read())) {
            if (!esl_seteq<1,1,1>(ap_const_logic_0, ap_sig_ioackin_SOURCE_BUS_WREADY.read())) {
                ap_reg_ioackin_SOURCE_BUS_WREADY = ap_const_logic_0;
            } else if (esl_seteq<1,1,1>(ap_const_logic_1, SOURCE_BUS_WREADY.read())) {
                ap_reg_ioackin_SOURCE_BUS_WREADY = ap_const_logic_1;
            }
        }
    }
    if ((esl_seteq<1,1,1>(ap_CS_fsm_state1.read(), ap_const_lv1_1) && 
         !esl_seteq<1,1,1>(ap_start.read(), ap_const_logic_0) && 
         esl_seteq<1,1,1>(ap_const_lv1_0, tmp_fu_120_p2.read()))) {
        p_0_reg_89 = ap_const_lv32_FFFFFFFF;
    } else if ((esl_seteq<1,1,1>(ap_const_lv1_1, ap_CS_fsm_state16.read()) && 
                !esl_seteq<1,1,1>(tmp_reg_137.read(), ap_const_lv1_0) && 
                !(!esl_seteq<1,1,1>(tmp_reg_137.read(), ap_const_lv1_0) && esl_seteq<1,1,1>(SOURCE_BUS_BVALID.read(), ap_const_logic_0)))) {
        p_0_reg_89 = tmp_1_reg_146.read();
    }
    if ((esl_seteq<1,1,1>(ap_const_lv1_1, ap_CS_fsm_state9.read()) && !esl_seteq<1,1,1>(SOURCE_BUS_RVALID.read(), ap_const_logic_0))) {
        SOURCE_BUS_addr_read_reg_141 = SOURCE_BUS_RDATA.read();
    }
    if ((esl_seteq<1,1,1>(ap_CS_fsm_state1.read(), ap_const_lv1_1) && !esl_seteq<1,1,1>(ap_start.read(), ap_const_logic_0))) {
        SOURCE_BUS_addr_reg_131 = tmp_2_fu_110_p1.read();
        tmp_reg_137 = tmp_fu_120_p2.read();
    }
    if ((esl_seteq<1,1,1>(ap_const_lv1_1, ap_CS_fsm_state10.read()) && !esl_seteq<1,1,1>(ap_const_logic_0, ap_sig_ioackin_SOURCE_BUS_AWREADY.read()))) {
        tmp_1_reg_146 = tmp_1_fu_126_p2.read();
    }
}

